import React, { useState } from "react";
import useInterval from "../utils/useInterval";
import ActiveDisplay from "./ActiveDisplay";
import Duration from "./Duration";
import PlayPause from "./PlayPause";


function Pomodoro() {
  // Timer starts out paused
  // const [isTimerRunning, setIsTimerRunning] = useState(false);
  const [timeModeMaster, setTimeModeMaster] = useState({
    focusCurrent: 25,
    focusMin: 5,
    focusMax: 60,
    focusIncrement: 5,
    focusCount: 25*60,
    breakCurrent: 5,
    breakMin: 1,
    breakMax: 15,
    breakIncrement: 1,
    breakCount: 5*60,
    mode: 'Focus',
    isTimerRunning: false
  });

  const { isTimerRunning } = timeModeMaster;

  useInterval(
    () => {
      // ToDo: Implement what should happen when the timer is running
    },
    isTimerRunning ? 1000 : null
  );

  function playPause() {
    const swap = !isTimerRunning;
    setTimeModeMaster(() => {
      return {...timeModeMaster, isTimerRunning: swap};
    });
  }

  return (
    <div className="pomodoro">
      <Duration />
      <PlayPause playPause={playPause} timeModeMaster={timeModeMaster} />
      <ActiveDisplay />
    </div>
  );
}

export default Pomodoro;
