import React from 'react';
import { minutesToDuration } from '../utils/duration/index';

export default function Duration(props){
    const { timeModeMaster, setTimeModeMaster, isTimerRunning, setIsTimerRunning } = props;
    const { focusCurrent, breakCurrent, focusIncrement } = timeModeMaster

    const btnHandler = (type) => {
        if(type === 'fMinus'){
            if(focusCurrent > 5 && focusCurrent < 60){
                const update = focusCurrent - focusIncrement
                setTimeModeMaster({...timeModeMaster, focusCurrent: update })
            }
        }
        if(type === 'fPlus'){

        }
        if(type === 'bMinus'){

        }
        if(type === 'bPlus'){

        }
    }

    return (
        <div className="row">
            <div className="col">
            <div className="input-group input-group-lg mb-2">
                <span className="input-group-text" data-testid="duration-focus">
                {/* TODO: Update this text to display the current focus session duration */}
                Focus Duration: {minutesToDuration(focusCurrent)}
                </span>
                <div className="input-group-append">
                {/* TODO: Implement decreasing focus duration and disable during a focus or break session */}
                <button
                    type="button"
                    className="btn btn-secondary"
                    data-testid="decrease-focus"
                    onClick={btnHandler('fMinus')}
                >
                    <span className="oi oi-minus" />
                </button>
                {/* TODO: Implement increasing focus duration  and disable during a focus or break session */}
                <button
                    type="button"
                    className="btn btn-secondary"
                    data-testid="increase-focus"
                    onClick={btnHandler('fPlus')}
                >
                    <span className="oi oi-plus" />
                </button>
                </div>
            </div>
            </div>
            <div className="col">
            <div className="float-right">
            <div className="input-group input-group-lg mb-2">
                <span className="input-group-text" data-testid="duration-break">
                {/* TODO: Update this text to display the current break session duration */}
                Break Duration: {minutesToDuration(breakCurrent)}
                </span>
                <div className="input-group-append">
                {/* TODO: Implement decreasing break duration and disable during a focus or break session*/}
                <button
                    type="button"
                    className="btn btn-secondary"
                    data-testid="decrease-break"
                    onClick={btnHandler('bMinus')}
                >
                    <span className="oi oi-minus" />
                </button>
                {/* TODO: Implement increasing break duration and disable during a focus or break session*/}
                <button
                    type="button"
                    className="btn btn-secondary"
                    data-testid="increase-break"
                    onClick={btnHandler('bPlus')}
                >
                    <span className="oi oi-plus" />
                </button>
                </div>
            </div>
            </div>
        </div>
      </div>
    );
}
